    private void btnEntrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEntrarActionPerformed
        try{  
            if(txtEmail.getText().isEmpty() || txtSenha.getText().isEmpty()){
                JOptionPane.showMessageDialog(null, "Preencha todos os campos!");
                return;
            }

            UsuarioDAO usuarioDAO = new UsuarioDAO();
            Usuario usuario = usuarioDAO.validarLogin(txtEmail.getText(), txtSenha.getText());

            if (usuario != null) {
                JOptionPane.showMessageDialog(this, "Bem-vindo, " + usuario.getNome() + "! (" + usuario.getTipoUsuario() + ")");
                Menu menu = new Menu();
                menu.setVisible(true);
                dispose();      
            } 
            else {
                JOptionPane.showMessageDialog(this, "Email ou senha inv√°lidos!");
            }
            
        } catch (Exception ex){
            JOptionPane.showMessageDialog(this, "Erro ao validar login: " + ex.getMessage());
        }
    }//GEN-LAST:event_btnEntrarActionPerformed

